 565	 // --- Admin/DAO Functions ---
 566	 function setAdmin(address newAdmin) external onlyTimelock {
 567	 require(newAdmin != address(0), "Invalid address");
 568	 timelock = newAdmin;
 569	 }
 570	
 571	 function getAdmin() external view returns (address) {
 572	 return timelock;
 573	 }
 574	
 575	 function setAllowedCollateral(
 576	 address token,
 577	 bool allowed
 578	 ) external onlyTimelock {
 579	 isAllowedCollateral[token] = allowed;
 580	
 581	 bool alreadyExists = false;
 582	 for (uint i = 0; i < collateralTokenList.length; i++) {
 583	 if (collateralTokenList[i] == token) {
 584	 alreadyExists = true;
 585	 break;
 586	 }
 587	 }
 588	
 589	 if (allowed && !alreadyExists) {
 590	 collateralTokenList.push(token);
 591	 }
 592	
 593	 emit CollateralTokenStatusChanged(token, allowed);
 594	 }
 595	
 596	 function depositCollateral(address token, uint256 amount) external {
 597	 if (!isAllowedCollateral[token]) {
 598	 emit UserError(msg.sender, "Token not allowed");
 599	 revert("Token not allowed");
 600	 }
 601	 if (amount == 0) {
 602	 emit UserError(msg.sender, "Amount must be > 0");
 603	 revert("Amount must be > 0");
 604	 }
 605	 if (isLiquidatable[msg.sender]) {
 606	 emit UserError(msg.sender, "Account is in liquidation");
 607	 revert("Account is in liquidation");
 608	 }
 609	
 610	 if (!isKnownUser[msg.sender]) {
 611	 isKnownUser[msg.sender] = true;
 612	 users.push(msg.sender);
 613	 }
 614	
 615	 IERC20(token).transferFrom(msg.sender, address(this), amount);
 616	 collateralBalance[token][msg.sender] += amount;
 617	
 618	 emit CollateralDeposited(msg.sender, token, amount);
 619	 }
 620	
 621	 function withdrawCollateral(address token, uint256 amount) external {
 622	 if (!isAllowedCollateral[token]) {
 623	 emit UserError(msg.sender, "Token not allowed");
 624	 revert("Token not allowed");
 625	 }
 626	 if (collateralBalance[token][msg.sender] < amount) {
 627	 emit UserError(msg.sender, "Insufficient balance");
 628	 revert("Insufficient balance");
 629	 }
 630	 if (isLiquidatable[msg.sender]) {
 631	 emit UserError(msg.sender, "Account is in liquidation");
 632	 revert("Account is in liquidation");
 633	 }
 634	
 635	 uint256 newCollateralValue = getTotalCollateralValue(msg.sender) -
 636	 ((amount * getTokenValue(token)) / 1e18);
 637	
 638	 if (
 639	 newCollateralValue * 100 <
 640	 userDebt[msg.sender] *
 641	 stablecoinManager.getLiquidationThreshold(token)
 642	 ) {
 643	 emit UserError(
 644	 msg.sender,
 645	 "Withdrawal would make position undercollateralized"
 646	 );
 647	 revert("Withdrawal would make position undercollateralized");
 648	 }
 649	
 650	 collateralBalance[token][msg.sender] -= amount;
 651	 IERC20(token).transfer(msg.sender, amount);
 652	
 653	 emit CollateralWithdrawn(msg.sender, token, amount);
 654	 }
 655	
 656	 function getCollateral(
 657	 address user,
 658	 address token
 659	 ) external view returns (uint256) {
 660	 return collateralBalance[token][user];
 661	 }
 662	
 663	 function getMyDebt() external view returns (uint256) {
 664	 return userDebt[msg.sender];
 665	 }
 666	
 667	 // Get user's risk tier
 668	 // Get user's risk tier (UPDATED to use RISC0 scores)
 669	 function getRiskTier(address user) public view returns (RiskTier) {
 670	 uint256 score = _getCreditScore(user); // Now uses RISC0 if available
 671	
 672	 for (uint256 i = 0; i < borrowTierConfigs.length; i++) {
 673	 if (
 674	 score >= borrowTierConfigs[i].minScore &&
 675	 score <= borrowTierConfigs[i].maxScore
 676	 ) {
 677	 return RiskTier(i);
 678	 }
 679	 }
 680	
 681	 return RiskTier(borrowTierConfigs.length - 1); // Default to lowest tier
 682	 }
 683	
 684	 // Admin function to update tier configurations
 685	 function updateBorrowTier(
 686	 uint256 tierIndex,
 687	 uint256 minScore,
 688	 uint256 maxScore,
 689	 uint256 collateralRatio,
 690	 int256 interestRateModifier,
 691	 uint256 maxLoanAmount
 692	 ) external onlyTimelock {
 693	 require(tierIndex < borrowTierConfigs.length, "Invalid tier");
 694	 borrowTierConfigs[tierIndex] = BorrowTierConfig(
 695	 minScore,
 696	 maxScore,
 697	 collateralRatio,
 698	 interestRateModifier,
 699	 maxLoanAmount
 700	 );
 701	 }
 702	
 703	 // Get complete tier configuration for a user
 704	 function getBorrowTerms(
 705	 address user
 706	 )
 707	 public
 708	 view
 709	 returns (
 710	 uint256 collateralRatio,
 711	 int256 interestRateModifier,
 712	 uint256 maxLoanAmount
 713	 )
 714	 {
 715	 RiskTier tier = getRiskTier(user);
 716	 BorrowTierConfig memory config = borrowTierConfigs[uint256(tier)];
 717	 return (
 718	 config.collateralRatio,
 719	 config.interestRateModifier,
 720	 (totalFunds * config.maxLoanAmount) / 100
 721	 );
 722	 }
 723	
 724	 // Get dynamic borrower rate for a user based on utilization and risk tier
 725	 function getBorrowerRate(address user) public view returns (uint256) {
 726	 uint256 totalSupplied = totalFunds;
 727	 uint256 totalBorrowed = totalBorrowedAllTime - totalRepaidAllTime;
 728	 uint256 utilization = totalSupplied > 0
 729	 ? (totalBorrowed * 1e18) / totalSupplied
 730	 : 0;
 731	 uint256 baseRate = interestRateModel.getBorrowRate(utilization);
 732	 (, int256 modifierBps, ) = getBorrowTerms(user);
 733	 uint256 adjustedRate = baseRate;
 734	 if (modifierBps < 0) {
 735	 adjustedRate = (baseRate * (10000 - uint256(-modifierBps))) / 10000;
 736	 } else if (modifierBps > 0) {
 737	 adjustedRate = (baseRate * (10000 + uint256(modifierBps))) / 10000;
 738	 }
 739	 return adjustedRate;
 740	 }
 741	
 742	 // Helper function to calculate dynamic rate
 743	 function _calculateBorrowRate(
 744	 uint256 amount,
 745	 RiskTier tier
 746	 ) internal view returns (uint256) {
 747	 uint256 totalSupplied = totalFunds;
 748	 uint256 totalBorrowed = totalBorrowedAllTime - totalRepaidAllTime;
 749	 uint256 utilization = 0;
 750	 if (totalSupplied > 0) {
 751	 utilization = (totalBorrowed * 1e18) / totalSupplied;
 752	 }
 753	 uint256 baseRate = interestRateModel.getBorrowRate(utilization);
 754	 int256 modifierBps = borrowTierConfigs[uint256(tier)]
 755	 .interestRateModifier;
 756	 uint256 adjustedRate = baseRate;
 757	 if (modifierBps < 0) {
 758	 adjustedRate = (baseRate * (10000 - uint256(-modifierBps))) / 10000;
 759	 } else if (modifierBps > 0) {
 760	 adjustedRate = (baseRate * (10000 + uint256(modifierBps))) / 10000;
 761	 }
 762	 return adjustedRate;
 763	 }
 764	
 765	 // Helper function to create loan
 766	 function _createLoan(uint256 amount, uint256 rate) internal {
 767	 uint256 installment = amount / 12;
 768	 uint256 nextDue = block.timestamp + 30 days;
 769	 loans[msg.sender] = Loan({
 770	 principal: amount,
 771	 outstanding: amount,
 772	 interestRate: rate,
 773	 nextDueDate: nextDue,
 774	 installmentAmount: installment,
 775	 penaltyBps: 500, // 5% default
 776	 active: true
 777	 });
 778	 }
 779	
 780	 function borrow(
 781	 uint256 amount, bytes32 nullifier
 782	 ) external payable whenNotPaused noReentrancy {
 783	 // 1. Check for existing debt
 784	 require(userDebt[msg.sender] == 0, "Repay your existing debt first");
 785	 require(!nullifierRegistry.isNullifierUsed(nullifier), "Proof already used!");
 786	 require(nullifierRegistry.hasSelectedAccounts(msg.sender), "Select accounts first");
 787	 nullifierRegistry.useNullifier(nullifier, msg.sender);
 788	
 789	
 790	
 791	 // 2. Get credit score (now uses RISC0 if available)
 792	 uint256 userCreditScore = _getCreditScore(msg.sender);
 793	 
 794	 // NEW: Log which credit score source was used
 795	 (uint256 score, string memory source, bool isVerified) = this.getCreditScoreWithSource(msg.sender);
 796	 emit CreditScoreSourceUsed(msg.sender, source, score, userCreditScore);
 797	
 798	 // 3. Check for credit score (TIER_5)
 799	 RiskTier tier = getRiskTier(msg.sender);
 800	 require(tier != RiskTier.TIER_5, "Credit score too low");
 801	
 802	 // 4. Check for available lending capacity (not more than half the pool)
 803	 require(
 804	 amount <= totalFunds / 2,
 805	 "Borrow amount exceeds available lending capacity"
 806	 );
 807	
 808	 // 5. Check for tier limit
 809	 (, , uint256 maxLoanAmount) = getBorrowTerms(msg.sender);
 810	 require(
 811	 amount <= maxLoanAmount,
 812	 "Borrow amount exceeds your tier limit"
 813	 );
 814	
 815	 // 6. Check for sufficient collateral
 816	 (uint256 requiredRatio, , ) = getBorrowTerms(msg.sender);
 817	 uint256 collateralValue = getTotalCollateralValue(msg.sender);
 818	 require(
 819	 collateralValue * 100 >= amount * requiredRatio,
 820	 "Insufficient collateral for this loan"
 821	 );
 822	
 823	 // 7. Calculate and apply origination fee
 824	 uint256 originationFee = 0;
 825	 if (reserveAddress != address(0)) {
 826	 originationFee =
 827	 (amount * tierFees[uint256(tier)].originationFee) /
 828	 10000;
 829	 }
 830	 uint256 netAmount = amount - originationFee;
 831	
 832	 // 8. Transfer origination fee to reserve if applicable
 833	 if (originationFee > 0) {
 834	 payable(reserveAddress).transfer(originationFee);
 835	 emit FeeCollected(
 836	 msg.sender,
 837	 originationFee,
 838	 "origination",
 839	 uint256(tier)
 840	 );
 841	 }
 842	
 843	 // 9. Calculate dynamic rate
 844	 uint256 adjustedRate = _calculateBorrowRate(amount, tier);
 845	
 846	 // 10. Create loan
 847	 require(amount >= 12, "Loan amount too small for amortization");
 848	 _createLoan(amount, adjustedRate);
 849	
 850	 // 11. Update state
 851	 userDebt[msg.sender] = amount;
 852	 borrowTimestamp[msg.sender] = block.timestamp;
 853	 borrowedAmountByRiskTier[tier] += amount;
 854	 totalBorrowedAllTime += amount;
 855	
 856	 // 12. Transfer net amount to borrower (after deducting origination fee)
 857	 payable(msg.sender).transfer(netAmount);
 858	
 859	 // TODO: set creditscore to used in the CreditScore.sol contract
 860	 // call a function passing the user
 861	
 862	 emit LoanDisbursed(msg.sender, amount, adjustedRate);
 863	 emit Borrowed(msg.sender, amount);
 864	 emit BorrowWithNullifier(msg.sender, amount, nullifier);
 865	 }
 866	
 867	 function repayInstallment() external payable whenNotPaused {
 868	 Loan storage loan = loans[msg.sender];
 869	 require(loan.active, "No active loan");
 870	 require(
 871	 msg.value >= loan.installmentAmount,
 872	 "Insufficient installment"
 873	 );
 874	 require(block.timestamp >= loan.nextDueDate, "Too early");
 875	
 876	 // Calculate late penalty using tier-specific late fee APR
 877	 uint256 penalty = 0;
 878	 if (block.timestamp > loan.nextDueDate + 7 days) {
 879	 RiskTier tier = getRiskTier(msg.sender);
 880	 uint256 lateFeeAPR = tierFees[uint256(tier)].lateFeeAPR;
 881	 uint256 daysLate = (block.timestamp - (loan.nextDueDate + 7 days)) /
 882	 1 days;
 883	 if (daysLate > 0 && lateFeeAPR > 0) {
 884	 penalty =
 885	 (loan.outstanding * lateFeeAPR * daysLate) /
 886	 365 /
 887	 10000;
 888	 loan.outstanding += penalty;
 889	 emit LoanLatePenaltyApplied(msg.sender, penalty);
 890	 }
 891	 }
 892	
 893	 loan.outstanding -= loan.installmentAmount;
 894	 userDebt[msg.sender] -= loan.installmentAmount;
 895	 totalRepaidAllTime += loan.installmentAmount;
 896	 loan.nextDueDate += 30 days;
 897	 emit LoanInstallmentPaid(msg.sender, msg.value, loan.outstanding);
 898	 if (loan.outstanding == 0) {
 899	 loan.active = false;
 900	 emit LoanFullyRepaid(msg.sender);
 901	 }
 902	 }
 903	
 904	 function repay() external payable whenNotPaused noReentrancy {
 905	 uint256 debt = userDebt[msg.sender];
 906	 require(debt > 0, "No debt to repay");
 907	 require(msg.value > 0, "Must send ETH");
 908	
 909	 // State changes BEFORE external calls
 910	 uint256 repayAmount = msg.value > debt ? debt : msg.value;
 911	 userDebt[msg.sender] -= repayAmount;
 912	 totalRepaidAllTime += repayAmount;
 913	
 914	 // Update borrowed amount by risk tier
 915	 RiskTier tier = getRiskTier(msg.sender);
 916	 borrowedAmountByRiskTier[tier] -= repayAmount;
 917	
 918	 // Clear liquidation status
 919	 if (isLiquidatable[msg.sender]) {
 920	 isLiquidatable[msg.sender] = false;
 921	 liquidationStartTime[msg.sender] = 0;
 922	 }
 923	
 924	 // External call after state changes
 925	 if (address(votingToken) != address(0)) {
 926	 votingToken.mint(msg.sender, repayAmount / 1e16);
 927	 }
 928	
 929	 // Refund excess
 930	 if (msg.value > debt) {
 931	 payable(msg.sender).transfer(msg.value - debt);
 932	 }
 933	
 934	 emit Repaid(msg.sender, repayAmount);
 935	 }
