 518	
 519	 function performUpkeep(bytes calldata performData) external override {
 520	 require(
 521	 block.timestamp > lastUpkeep + UPKEEP_COOLDOWN,
 522	 "Upkeep throttled"
 523	 );
 524	 lastUpkeep = block.timestamp;
 525	 address[] memory liquidatableUsers = abi.decode(
 526	 performData,
 527	 (address[])
 528	 );
 529	
 530	 for (uint i = 0; i < liquidatableUsers.length; i++) {
 531	 address user = liquidatableUsers[i];
 532	 if (isLiquidatable[user]) {
 533	 uint256 deadline = liquidationStartTime[user] + GRACE_PERIOD;
 534	 if (block.timestamp >= deadline) {
 535	 lendingManager.executeLiquidation(address(this), user);
 536	 }
 537	 }
 538	 }
 539	 }
