 1070	 function startLiquidation(address user) external {
 1071	 (bool isHealthy, ) = checkCollateralization(user);
 1072	 require(!isHealthy, "Position is healthy");
 1073	 require(!isLiquidatable[user], "Liquidation already started");
 1074	
 1075	 isLiquidatable[user] = true;
 1076	 liquidationStartTime[user] = block.timestamp;
 1077	
 1078	 emit LiquidationStarted(user);
 1079	 }
