 1085	 collateralBalance[token][msg.sender] += amount;
 1086	
 1087	 (bool isHealthy, ) = checkCollateralization(msg.sender);
 1088	 if (isHealthy) {
 1089	 isLiquidatable[msg.sender] = false;
 1090	 liquidationStartTime[msg.sender] = 0;
 1091	 }
