 22	async function debugTiming(governor, proposalId) {
 23	 let proposal;
 24	 try {
 25	 proposal = await governor.proposals(proposalId);
 26	 } catch {
 27	 // fallback for standard Governor proposals that don't have proposals mapping
 28	 proposal = {};
 29	 }
 30	 const now = (await ethers.provider.getBlock("latest")).timestamp;
 31	 console.log(`\nProposal ${proposalId} Timing:`);
 32	 console.log(`- Now: ${now} (${new Date(now * 1000)})`);
 33	 if (proposal.startTime) {
 34	 console.log(`- Start: ${proposal.startTime} (${new Date(proposal.startTime * 1000)})`);
 35	 console.log(`- End: ${proposal.endTime} (${new Date(proposal.endTime * 1000)})`);
 36	 console.log(`- Time remaining: ${proposal.endTime - now} seconds`);
 37	 console.log(`- Executed: ${proposal.executed}`);
 38	 }
 39	}
