 41	async function executeGovernanceProposal(governor, targets, values, calldatas, description, accounts, maxAccounts, network) {
 42	 // 1. Get current block
 43	 const currentBlock = await ethers.provider.getBlock("latest");
 44	 console.log(`Current block time: ${currentBlock.timestamp}`);
 45	
 46	 // 2. Create proposal
 47	 console.log('Creating proposal...');
 48	 const proposeTx = await governor.propose(
 49	 targets,
 50	 values,
 51	 calldatas,
 52	 description,
 53	 { gasLimit: 500000 }
 54	 );
 55	 const proposeReceipt = await proposeTx.wait();
 56	 const proposalId = proposeReceipt.logs.find(log => {
 57	 try {
 58	 const parsed = governor.interface.parseLog(log);
 59	 return parsed.name === 'ProposalCreated';
 60	 } catch {
 61	 return false;
 62	 }
 63	 }).args.proposalId;
 64	 console.log(`Proposal created with ID: ${proposalId}`);
 65	
 66	 // 3. Get proposal details (try proposals mapping, fallback to votingPeriod)
 67	 let proposal;
 68	 try {
 69	 proposal = await governor.proposals(proposalId);
 70	 } catch {
 71	 proposal = {};
 72	 }
 73	 let votingPeriodSec;
 74	 if (proposal.startTime && proposal.endTime) {
 75	 votingPeriodSec = proposal.endTime - proposal.startTime;
 76	 console.log(`Proposal start: ${proposal.startTime}`);
 77	 console.log(`Proposal end: ${proposal.endTime}`);
 78	 } else {
 79	 // fallback: use votingPeriod blocks * average block time (assume 1s for dev)
 80	 const votingPeriodBlocks = await governor.votingPeriod();
 81	 votingPeriodSec = Number(votingPeriodBlocks);
 82	 console.log(`Voting period (blocks): ${votingPeriodBlocks}`);
 83	 }
 84	
 85	 // 4. Mine exactly 1 block to activate proposal
 86	 await network.provider.send("evm_mine");
 87	 let state = await governor.state(proposalId);
 88	 console.log(`Proposal state: ${state}`); // Should be 1 (Active)
 89	
 90	 // 5. Vote immediately with all accounts
 91	 console.log('Voting on proposal...');
 92	 for (let j = 0; j < maxAccounts; j++) {
 93	 const voteTx = await governor.connect(accounts[j]).castVote(proposalId, 1, { gasLimit: 200000 });
 94	 await voteTx.wait();
 95	 console.log(`Account ${j} voted`);
 96	 }
 97	
 98	 // 6. Calculate exact time remaining in voting period
 99	 const afterVoteBlock = await ethers.provider.getBlock("latest");
 100	 let timeElapsed;
 101	 let timeRemaining;
 102	 if (proposal.startTime && proposal.endTime) {
 103	 timeElapsed = afterVoteBlock.timestamp - proposal.startTime;
 104	 timeRemaining = proposal.endTime - afterVoteBlock.timestamp;
 105	 } else {
 106	 // fallback: use votingPeriodSec
 107	 timeElapsed = 0;
 108	 timeRemaining = votingPeriodSec - 1;
 109	 }
 110	 console.log(`Voting period: ${votingPeriodSec} seconds`);
 111	 console.log(`Time elapsed: ${timeElapsed} seconds`);
 112	 console.log(`Time remaining: ${timeRemaining} seconds`);
 113	
 114	 // 7. Fast-forward to just before voting ends
 115	 if (timeRemaining > 1) {
 116	 console.log(`Fast-forwarding ${timeRemaining - 1} seconds`);
 117	 await network.provider.send("evm_increaseTime", [timeRemaining - 1]);
 118	 await network.provider.send("evm_mine");
 119	 }
 120	
 121	 // 8. Mine enough blocks to end the voting period (ensure proposal moves to Succeeded)
 122	 let votingBlocks = 20; // fallback default
 123	 try {
 124	 votingBlocks = Number(await governor.votingPeriod());
 125	 } catch { }
 126	 for (let i = 0; i < votingBlocks + 2; i++) {
 127	 await network.provider.send("evm_mine");
 128	 }
 129	 state = await governor.state(proposalId);
 130	 console.log(`Proposal state after deadline: ${state}`); // Should be 4 (Succeeded)
 131	
 132	 // 9. Queue the proposal
 133	 console.log('Queueing proposal...');
 134	 const descriptionHash = ethers.id(description);
 135	 state = await governor.state(proposalId);
 136	 const currentBlockTs = (await ethers.provider.getBlock('latest')).timestamp;
 137	 const snapshot = await governor.proposalSnapshot(proposalId);
 138	 const deadline = await governor.proposalDeadline(proposalId);
 139	 console.log("Proposal ID:", proposalId.toString());
 140	 console.log("State before queue:", state);
 141	 console.log("Description hash:", descriptionHash);
 142	 console.log("Current block timestamp:", currentBlockTs);
 143	 console.log("Proposal snapshot:", snapshot);
 144	 console.log("Proposal deadline:", deadline);
 145	 if (state !== 4) throw new Error("Proposal not in Succeeded state before queue");
 146	 const queueTx = await governor.queue(
 147	 targets,
 148	 values,
 149	 calldatas,
 150	 descriptionHash,
 151	 { gasLimit: 500000 }
 152	 );
 153	 await queueTx.wait();
 154	
 155	 // 10. Fast-forward timelock delay
 156	 const timelock = await ethers.getContractAt("TimelockController", await governor.timelock());
 157	 const delay = await timelock.getMinDelay();
 158	 console.log(`Fast-forwarding ${delay} seconds for timelock...`);
 159	 await network.provider.send("evm_increaseTime", [Number(delay) + 1]);
 160	 await network.provider.send("evm_mine");
 161	
 162	 // 11. Execute proposal
 163	 state = await governor.state(proposalId);
 164	 console.log("State before execute:", state);
 165	 if (state !== 5) throw new Error("Proposal not in Queued state before execute");
 166	 console.log('Executing proposal...');
 167	 const executeTx = await governor.execute(
 168	 targets,
 169	 values,
 170	 calldatas,
 171	 descriptionHash,
 172	 { gasLimit: 500000 }
 173	 );
 174	 await executeTx.wait();
 175	
 176	 // 12. Verify final state
 177	 state = await governor.state(proposalId);
 178	 console.log(`Final proposal state: ${state}`); // Should be 7 (Executed)
 179	}
 180	
 181	function getStateName(state) {
 182	 const states = [
 183	 "Pending",
 184	 "Active",
 185	 "Canceled",
 186	 "Defeated",
 187	 "Succeeded",
 188	 "Queued",
 189	 "Expired",
 190	 "Executed"
 191	 ];
 192	 return states[state] || "Unknown";
 193	}
 194	
 195	// Helper to generate a unique description for each proposal
 196	function makeUniqueDescription(base) {
 197	 return base + ' [' + Date.now() + '-' + Math.floor(Math.random() * 1e6) + ']';
 198	}
 199	
 200	// Helper: BigInt square root for quadratic voting
 201	function sqrtBigInt(n) {
 202	 if (n < 0n) throw 'square root of negative numbers is not supported';
 203	 if (n < 2n) return n;
 204	 function newtonIteration(n, x0) {
 205	 const x1 = (x0 + n / x0) >> 1n;
 206	 if (x0 === x1 || x0 === x1 - 1n) return x0;
 207	 return newtonIteration(n, x1);
 208	 }
 209	 return newtonIteration(n, 1n);
 210	}
