 188	 transactions.sort((a, b) => b.timestamp - a.timestamp)
 189	
 190	 console.log("Total transactions found:", transactions.length)
 191	
 192	 } catch (error) {
 193	 console.error("Error fetching transaction history:", error)
 194	 throw error
 195	 }
 196	
 197	 return transactions
 198	}
