 219	 event LoanDisbursed(address indexed borrower, uint256 amount, uint256 rate);
 220	 event LoanInstallmentPaid(
 221	 address indexed borrower,
 222	 uint256 amount,
 223	 uint256 remaining
 224	 );
 225	 event LoanFullyRepaid(address indexed borrower);
 226	 event LoanLatePenaltyApplied(address indexed borrower, uint256 penalty);
 227	
 228	 modifier noReentrancy() {
 229	 require(!locked, "No reentrancy");
 230	 locked = true;
 231	 _;
 232	 locked = false;
 233	 }
 234	
 235	 modifier validAddress(address _addr) {
 236	 require(_addr != address(0), "Invalid address: zero address");
 237	 require(_addr != address(this), "Invalid address: self");
 238	 _;
 239	 }
 240	
 241	 modifier whenNotPaused() {
 242	 require(!paused, "Contract is paused");
 243	 _;
 244	 }
 245	
 246	 modifier requiresZKProof() {
 247	 if (zkProofRequired && address(creditSystem) != address(0)) {
 248	 require(
 249	 creditSystem.isEligibleToBorrow(msg.sender),
 250	 "ZK proof verification required"
 251	 );
 252	 }
 253	 _;
 254	 }
 255	
 256	 modifier onlyTimelock() {
 257	 if (msg.sender != timelock) revert OnlyTimelockLiquidityPool();
 258	 _;
 259	 }
 260	
 261	 function initialize(
 262	 address _timelock,
 263	 address _stablecoinManager,
 264	 address _lendingManager,
 265	 address _interestRateModel,
 266	 address _creditSystem,
 267	 address _nullifierRegistry
 268	 ) public initializer {
 269	 __AccessControl_init();
 270	 timelock = _timelock;
 271	 stablecoinManager = StablecoinManager(_stablecoinManager);
 272	 lendingManager = LendingManager(payable(_lendingManager));
 273	 interestRateModel = InterestRateModel(_interestRateModel);
 274	 nullifierRegistry = NullifierRegistry(_nullifierRegistry);
 275	
 276	 // Initialize ZK-proof system
 277	 if (_creditSystem != address(0)) {
 278	 creditSystem = IntegratedCreditSystem(_creditSystem);
 279	 zkProofRequired = true; // Enable ZK proof requirement by default
 280	 }
 281	
 282	 // NEW: Initialize RISC0 integration
 283	 useRISC0CreditScores = false; // Disabled by default until contract is set
 284	
 285	 _initializeRiskTiers();
 286	 minPartialLiquidationAmount = 1e16;
 287	 }
 288	
 289	 // Initialize the risk tier system (should be called in initialize function)
 290	 function _initializeRiskTiers() internal {
 291	 // Tier 1: 90-100 score, 110% collateral, 25% discount, can borrow up to 50% of pool
 292	 borrowTierConfigs.push(BorrowTierConfig(90, 100, 110, -25, 50));
 293	
 294	 // Tier 2: 80-89 score, 125% collateral, 10% discount, can borrow up to 40% of pool
 295	 borrowTierConfigs.push(BorrowTierConfig(80, 89, 125, -10, 40));
 296	
 297	 // Tier 3: 70-79 score, 140% collateral, standard rate, can borrow up to 30% of pool
 298	 borrowTierConfigs.push(BorrowTierConfig(70, 79, 140, 0, 30));
 299	
 300	 // Tier 4: 60-69 score, 160% collateral, 15% premium, can borrow up to 20% of pool
 301	 borrowTierConfigs.push(BorrowTierConfig(60, 69, 160, 15, 20));
 302	
 303	 // Tier 5: 0-59 score, not eligible for standard borrowing
 304	 borrowTierConfigs.push(BorrowTierConfig(0, 59, 200, 30, 0));
 305	 }
