 307	 // NEW: RISC0 Credit Score Integration Functions
 308	
 309	 /**
 310	 * @notice Set the RISC0 credit score contract address
 311	 * @param _creditScoreContract Address of the CreditScore contract
 312	 */
 313	 function setCreditScoreContract(address _creditScoreContract) external onlyTimelock {
 314	 address oldContract = address(creditScoreContract);
 315	 creditScoreContract = ICreditScore(_creditScoreContract);
 316	 
 317	 // Auto-enable RISC0 scores if a valid contract is set
 318	 if (_creditScoreContract != address(0)) {
 319	 useRISC0CreditScores = true;
 320	 }
 321	 
 322	 emit CreditScoreContractUpdated(oldContract, _creditScoreContract);
 323	 }
 324	
 325	 /**
 326	 * @notice Toggle RISC0 credit score usage
 327	 * @param _useRISC0 Whether to use RISC0 scores
 328	 */
 329	 function toggleRISC0CreditScores(bool _useRISC0) external onlyTimelock {
 330	 useRISC0CreditScores = _useRISC0;
 331	 emit RISC0ScoreToggled(_useRISC0);
 332	 }
 333	
 334	 /**
 335	 * @notice Convert FICO score (300-850) to contract score (0-100)
 336	 * @param ficoScore FICO score from RISC0 verification
 337	 * @return Contract score (0-100)
 338	 */
 339	 function convertFICOToContractScore(uint64 ficoScore) public pure returns (uint256) {
 340	 if (ficoScore <= 300) return 0;
 341	 if (ficoScore >= 850) return 100;
 342	 
 343	 // Linear mapping: (FICO - 300) / 550 * 100
 344	 return ((ficoScore - 300) * 100) / 550;
 345	 }
 346	
 347	 /**
 348	 * @notice Enhanced credit score retrieval with RISC0 integration
 349	 * @param user Address of the user
 350	 * @return score Credit score (0-100)
 351	 * @return source Source of the credit score
 352	 * @return isVerified Whether the score is RISC0 verified
 353	 */
 354	 function getCreditScoreWithSource(address user) external view returns (
 355	 uint256 score,
 356	 string memory source,
 357	 bool isVerified
 358	 ) {
 359	 // Try RISC0 verified score first
 360	 if (useRISC0CreditScores && address(creditScoreContract) != address(0)) {
 361	 try creditScoreContract.getCreditScore(user) returns (
 362	 uint64 ficoScore,
 363	 bool isValid,
 364	 uint256 timestamp
 365	 ) {
 366	 if (isValid && ficoScore > 0) {
 367	 // Check if score is not expired
 368	 if (block.timestamp <= timestamp + SCORE_EXPIRY_PERIOD) {
 369	 uint256 convertedScore = convertFICOToContractScore(ficoScore);
 370	 return (convertedScore, "RISC0_VERIFIED", true);
 371	 }
 372	 }
 373	 } catch {
 374	 // Fall through to next source
 375	 }
 376	 }
 377	
 378	 // Try IntegratedCreditSystem
 379	 if (address(creditSystem) != address(0)) {
 380	 try creditSystem.getUserCreditProfile(user) returns (
 381	 bool hasTradFi,
 382	 bool hasAccount,
 383	 bool hasNesting,
 384	 uint256 finalScore,
 385	 bool isEligible,
 386	 uint256 lastUpdate
 387	 ) {
 388	 if (finalScore > 0) {
 389	 return (finalScore, "INTEGRATED_SYSTEM", false);
 390	 }
 391	 } catch {
 392	 // Fall through to final source
 393	 }
 394	 }
 395	
 396	 // Use local stored score as final fallback
 397	 uint256 localScore = creditScore[user];
 398	 return (localScore, "LOCAL_STORAGE", false);
 399	 }
 400	
 401	 /**
 402	 * @notice Internal function to get credit score with RISC0 priority
 403	 * @param user Address of the user
 404	 * @return Credit score (0-100)
 405	 */
 406	 function _getCreditScore(address user) internal view returns (uint256) {
 407	 // Try RISC0 verified score first
 408	 if (useRISC0CreditScores && address(creditScoreContract) != address(0)) {
 409	 try creditScoreContract.getCreditScore(user) returns (
 410	 uint64 ficoScore,
 411	 bool isValid,
 412	 uint256 timestamp
 413	 ) {
 414	 if (isValid && ficoScore > 0) {
 415	 // Check if score is not expired
 416	 if (block.timestamp <= timestamp + SCORE_EXPIRY_PERIOD) {
 417	 return convertFICOToContractScore(ficoScore);
 418	 }
 419	 }
 420	 } catch {
 421	 // Fall through to existing logic
 422	 }
 423	 }
 424	 
 425	 // Existing fallback logic from your original contract
 426	 if (address(creditSystem) != address(0)) {
 427	 try creditSystem.getUserCreditProfile(user) returns (
 428	 bool hasTradFi,
 429	 bool hasAccount,
 430	 bool hasNesting,
 431	 uint256 finalScore,
 432	 bool isEligible,
 433	 uint256 lastUpdate
 434	 ) {
 435	 if (finalScore > 0) {
 436	 return finalScore;
 437	 }
 438	 } catch {
 439	 // Fall back to stored score if ZK system fails
 440	 }
 441	 }
 442	 return creditScore[user];
 443	 }
 444	
 445	 /**
 446	 * @notice Check if user has a valid RISC0 verified credit score
 447	 * @param user Address of the user
 448	 * @return hasValidScore Whether user has valid RISC0 score
 449	 * @return score The RISC0 verified score
 450	 * @return timestamp When the score was verified
 451	 */
 452	 function hasValidRISC0Score(address user) external view returns (
 453	 bool hasValidScore,
 454	 uint256 score,
 455	 uint256 timestamp
 456	 ) {
 457	 if (!useRISC0CreditScores || address(creditScoreContract) == address(0)) {
 458	 return (false, 0, 0);
 459	 }
 460	
 461	 try creditScoreContract.getCreditScore(user) returns (
 462	 uint64 ficoScore,
 463	 bool isValid,
 464	 uint256 scoreTimestamp
 465	 ) {
 466	 if (isValid && ficoScore > 0 && block.timestamp <= scoreTimestamp + SCORE_EXPIRY_PERIOD) {
 467	 return (true, convertFICOToContractScore(ficoScore), scoreTimestamp);
 468	 }
 469	 } catch {
 470	 // Return false if call fails
 471	 }
 472	 
 473	 return (false, 0, 0);
 474	 }
 475	
 476	 function getAllUsers() public view returns (address[] memory) {
 477	 return users;
 478	 }
 479	
 480	 function checkUpkeep(
 481	 bytes calldata
 482	 )
 483	 external
 484	 view
 485	 override
 486	 returns (bool upkeepNeeded, bytes memory performData)
 487	 {
 488	 require(
 489	 block.timestamp > lastUpkeep + UPKEEP_COOLDOWN,
 490	 "Upkeep throttled"
 491	 );
 492	 address[] memory candidates = getAllUsers();
 493	 address[] memory toLiquidate = new address[](candidates.length);
 494	 uint count = 0;
 495	
 496	 for (uint i = 0; i < candidates.length; i++) {
 497	 address user = candidates[i];
 498	 if (isLiquidatable[user]) {
 499	 uint256 deadline = liquidationStartTime[user] + GRACE_PERIOD;
 500	 if (block.timestamp >= deadline) {
 501	 toLiquidate[count] = user;
 502	 count++;
 503	 }
 504	 }
 505	 }
 506	
 507	 if (count > 0) {
 508	 address[] memory result = new address[](count);
 509	 for (uint j = 0; j < count; j++) {
 510	 result[j] = toLiquidate[j];
 511	 }
 512	 upkeepNeeded = true;
 513	 performData = abi.encode(result);
 514	 } else {
 515	 upkeepNeeded = false;
 516	 }
 517	 }
 518	
 519	 function performUpkeep(bytes calldata performData) external override {
 520	 require(
 521	 block.timestamp > lastUpkeep + UPKEEP_COOLDOWN,
 522	 "Upkeep throttled"
 523	 );
 524	 lastUpkeep = block.timestamp;
 525	 address[] memory liquidatableUsers = abi.decode(
 526	 performData,
 527	 (address[])
 528	 );
 529	
 530	 for (uint i = 0; i < liquidatableUsers.length; i++) {
 531	 address user = liquidatableUsers[i];
 532	 if (isLiquidatable[user]) {
 533	 uint256 deadline = liquidationStartTime[user] + GRACE_PERIOD;
 534	 if (block.timestamp >= deadline) {
 535	 lendingManager.executeLiquidation(address(this), user);
 536	 }
 537	 }
 538	 }
 539	 }
