 1216	 // --- Price feed with staleness check ---
 1217	 function _getFreshPrice(
 1218	 address token
 1219	 ) internal view returns (uint256 price, uint256 updatedAt) {
 1220	 address feedAddress = priceFeed[token];
 1221	 require(feedAddress != address(0), "Price feed not set");
 1222	 AggregatorV3Interface pf = AggregatorV3Interface(feedAddress);
 1223	 (
 1224	 uint80 roundId,
 1225	 int256 answer,
 1226	 ,
 1227	 uint256 updatedAt_,
 1228	 uint80 answeredInRound
 1229	 ) = pf.latestRoundData();
 1230	 if (block.timestamp - updatedAt_ > _getMaxPriceAge(token)) {
 1231	 revert("Stale price");
 1232	 }
 1233	 require(answeredInRound >= roundId, "Stale round data");
 1234	 return (uint256(answer), updatedAt_);
 1235	 }
