 766	 function _createLoan(uint256 amount, uint256 rate) internal {
 767	 uint256 installment = amount / 12;
 768	 uint256 nextDue = block.timestamp + 30 days;
 769	 loans[msg.sender] = Loan({
 770	 principal: amount,
 771	 outstanding: amount,
 772	 interestRate: rate,
 773	 nextDueDate: nextDue,
 774	 installmentAmount: installment,
 775	 penaltyBps: 500, // 5% default
 776	 active: true
 777	 });
 778	 }
