 130	 function borrow(uint256 amount) external whenNotPaused {
 131	 require(creditScores[msg.sender] >= 50, "Credit score too low");
 132	 require(address(this).balance >= amount, "Insufficient pool balance");
 133	 require(amount <= totalSupply / 2, "Borrow amount too high");
 134	
 135	 userDebt[msg.sender] += amount;
 136	 totalBorrows += amount;
 137	 totalFunds -= amount;
 138	
 139	 loans[msg.sender] = Loan({
 140	 amount: amount,
 141	 timestamp: block.timestamp,
 142	 active: true
 143	 });
 144	
 145	 payable(msg.sender).transfer(amount);
 146	 emit Borrow(msg.sender, amount);
