 148	
 149	 function repay() external payable whenNotPaused {
 150	 require(msg.value > 0, "Amount must be greater than 0");
 151	 require(userDebt[msg.sender] > 0, "No debt to repay");
 152	
 153	 uint256 debt = userDebt[msg.sender];
 154	 uint256 repayAmount = msg.value;
 155	
 156	 if (repayAmount >= debt) {
 157	 userDebt[msg.sender] = 0;
 158	 totalBorrows -= debt;
 159	 totalFunds += debt;
 160	 loans[msg.sender].active = false;
 161	
 162	 if (repayAmount > debt) {
 163	 payable(msg.sender).transfer(repayAmount - debt);
 164	 }
 165	 emit Repay(msg.sender, debt);
 166	 } else {
 167	 userDebt[msg.sender] -= repayAmount;
 168	 totalBorrows -= repayAmount;
 169	 totalFunds += repayAmount;
 170	 emit Repay(msg.sender, repayAmount);
 171	 }
 172	 }
