 419	 function claimInterest() external nonReentrant {
 420	 require(isLender(msg.sender), "Not a lender");
 421	 require(lenders[msg.sender].isActive, "Not an active lender");
 422	 LenderInfo storage lender = lenders[msg.sender];
 423	 if (lender.balance == 0) {
 424	 revert("No funds deposited");
 425	 }
