 740	 function executeLiquidation(address pool, address user) external {
 741	 require(
 742	 ILiquidityPool(pool).isLiquidatable(user),
 743	 "Account not marked for liquidation"
 744	 );
 745	 require(
 746	 block.timestamp >=
 747	 ILiquidityPool(pool).liquidationStartTime(user) +
 748	 ILiquidityPool(pool).GRACE_PERIOD(),
 749	 "Grace period not ended"
 750	 );
 751	 address[] memory tokens = ILiquidityPool(pool)
 752	 .getAllowedCollateralTokens();
 753	 for (uint i = 0; i < tokens.length; i++) {
 754	 require(
 755	 ILiquidityPool(pool).isOracleHealthy(tokens[i]),
 756	 "Oracle circuit breaker triggered"
 757	 );
 758	 }
 759	 uint256 debt = ILiquidityPool(pool).userDebt(user);
 760	 uint256 penalty = (debt * ILiquidityPool(pool).LIQUIDATION_PENALTY()) /
 761	 100;
 762	 uint256 totalToRepay = debt + penalty;
 763	 for (uint i = 0; i < tokens.length; i++) {
 764	 address token = tokens[i];
 765	 uint256 balance = ILiquidityPool(pool).collateralBalance(
 766	 token,
 767	 user
 768	 );
 769	 if (balance > 0) {
 770	 ILiquidityPool(pool).clearCollateral(
 771	 token,
 772	 user,
 773	 msg.sender,
 774	 balance
 775	 );
 776	 }
 777	 }
 778	 ILiquidityPool(pool).clearDebt(user, debt);
 779	 emit LiquidationExecuted(user, msg.sender, totalToRepay);
 780	 }
 781	
 782	 function executePartialLiquidation(
 783	 address pool,
 784	 address user,
 785	 address collateralToken
 786	 ) external {
 787	 require(isUndercollateralized(pool, user), "Position healthy");
 788	 require(
 789	 ILiquidityPool(pool).isAllowedCollateral(collateralToken),
 790	 "Invalid collateral"
 791	 );
 792	 uint256 debt = ILiquidityPool(pool).userDebt(user);
 793	 uint256 price = ILiquidityPool(pool).getTokenValue(collateralToken);
 794	 uint256 ltv = ILiquidityPool(pool).getLTV(collateralToken);
 795	 require(ltv > 0, "LTV not set");
 796	 require(price > 0, "Collateral price is zero");
 797	 uint256 buffer = ILiquidityPool(pool).SAFETY_BUFFER();
 798	 uint256 numerator = debt * (100 + buffer) * 1e18;
 799	 uint256 denominator = ltv * price;
 800	 require(denominator != 0, "Division by zero in partial liquidation");
 801	 uint256 collateralToSeize = numerator / denominator;
 802	 uint256 userBalance = ILiquidityPool(pool).collateralBalance(
 803	 collateralToken,
 804	 user
 805	 );
 806	 if (collateralToSeize > userBalance) collateralToSeize = userBalance;
 807	 require(
 808	 collateralToSeize >=
 809	 ILiquidityPool(pool).minPartialLiquidationAmount(),
 810	 "Below min liquidation"
 811	 );
 812	 ILiquidityPool(pool).clearCollateral(
 813	 collateralToken,
 814	 user,
 815	 msg.sender,
 816	 collateralToSeize
 817	 );
 818	 ILiquidityPool(pool).clearDebt(user, debt);
 819	 emit PartialLiquidation(
 820	 user,
 821	 msg.sender,
 822	 collateralToken,
 823	 collateralToSeize,
 824	 debt
 825	 );
 826	 }
