 254	 await votingToken.waitForDeployment();
 255	 console.log("VotingToken deployed at:", await votingToken.getAddress());
 256	 console.log(`[DEPLOYED] VotingToken at: ${await votingToken.getAddress()} (new deployment)`);
 257	
