 577	 const GlintToken = await ethers.getContractFactory("GlintToken");
 578	 const glintToken = await GlintToken.deploy(ethers.parseEther('1000000'));
 579	 await glintToken.waitForDeployment();
 580	 const glintTokenAddress = await glintToken.getAddress();
 581	 console.log("GlintToken deployed at:", glintTokenAddress);
 582	
