 584	 console.log("\nDeploying MockPriceFeed for GlintToken...");
 585	 const glintMockFeed = await MockPriceFeed.deploy(
 586	 ethers.parseUnits("1.00", 8), // 1.00 with 8 decimals
 587	 8
 588	 );
 589	 await glintMockFeed.waitForDeployment();
 590	 const glintMockFeedAddress = await glintMockFeed.getAddress();
 591	 console.log("MockPriceFeed for GlintToken deployed to:", glintMockFeedAddress);
