 597	 console.log("VotingToken:", await votingToken.getAddress());
 598	 console.log("TimelockController:", await timelock.getAddress());
 599	 console.log("ProtocolGovernor:", await governor.getAddress());
 600	 console.log("StablecoinManager:", stablecoinManagerAddress);
 601	 console.log("InterestRateModel:", interestRateModelAddress);
 602	 console.log("LiquidityPool:", await liquidityPool.getAddress());
