 613	 TimelockController: await timelock.getAddress(),
 614	 ProtocolGovernor: await governor.getAddress(),
 615	 StablecoinManager: stablecoinManagerAddress,
 616	 InterestRateModel: interestRateModelAddress,
 617	 LiquidityPool: await liquidityPool.getAddress(),
