 29	// Helper function to setup collateral for borrowing
 30	async function setupCollateralForBorrowing(liquidityPool, glintToken, mockFeedGlint, user, borrowAmount, deployer, nullifierRegistry) {
 31	 const requiredRatio = 140; // Default for most tiers
 32	 const depositAmount = (borrowAmount * requiredRatio) / (100 * 110) / 100; // 10% buffer
 33	
 34	 await glintToken.transfer(user.address, depositAmount);
 35	 await glintToken.connect(user).approve(await liquidityPool.getAddress(), depositAmount);
 36	 await liquidityPool.connect(user).depositCollateral(await glintToken.getAddress(), depositAmount);
 37	
 38	 // Verify collateral value
 39	 const contractValue = await liquidityPool.getTotalCollateralValue(user.address);
 40	 const requiredValue = (borrowAmount * requiredRatio) / 100;
 41	 if (contractValue < requiredValue) throw new Error("Insufficient collateral in contract");
 42	
 43	 // Setup accounts for nullifier registry if not already done
 44	 const hasSelected = await nullifierRegistry.hasSelectedAccounts(user.address);
 45	 if (!hasSelected) {
 46	 await nullifierRegistry.connect(user).selectAccounts([user.address, deployer.address]);
 47	 }
 48	
 49	 // Ensure user has enough ETH for potential fees
 50	 await deployer.sendTransaction({
 51	 to: user.address,
 52	 value: ethers.parseEther("1")
 53	 });
 54	 return { depositAmount, requiredRatio };
 55	}
