 4	export interface Transaction {
 5	 id: string
 6	 type: 'deposit' | 'withdraw' | 'borrow' | 'repay' | 'lend' | 'liquidate' | 'interest'
 7	 amount: string
 8	 token?: string
 9	 status: 'pending' | 'confirmed' | 'failed'
 10	 timestamp: number
 11	 hash?: string
 12	 description: string
 13	 collateralRatio?: string
 14	 interestEarned?: string
 15	 blockNumber?: number
 16	}
