 18	export async function fetchTransactionHistory(
 19	 contract: any,
 20	 account: string,
 21	 provider: ethers.Provider
 22	): Promise<Transaction[]> {
 23	 const transactions: Transaction[] = []
 24	
 25	 try {
 26	 console.log("Fetching transaction history for account:", account)
 27	
 28	 // Get the current block number
 29	 const currentBlock = await provider.getBlockNumber()
 30	 console.log("Current block:", currentBlock)
 31	
 32	 // Fetch events from the last 10000 blocks (increased range to catch more transactions)
 33	 const fromBlock = Math.max(0, currentBlock - 10000)
 34	 console.log("Querying from block:", fromBlock, "to block:", currentBlock)
...
 129	
 130	 // Process repay events
 131	 for (const event of events[3]) {
 132	 try {
 133	 const block = await provider.getBlock(event.blockNumber)
 134	 transactions.push({
 135	 id: `${event.transactionHash}-repay`,
 136	 type: 'repay',
 137	 amount: formatEther(event.args.amount),
 138	 status: 'confirmed',
 139	 timestamp: block?.timestamp ? block.timestamp * 1000 : Date.now(),
 140	 hash: event.transactionHash,
 141	 description: `Repaid ${formatEther(event.args.amount)} ETH`,
 142	 blockNumber: event.blockNumber
 143	 })
 144	 } catch (err) {
 145	 console.error("Error processing repay event:", err)
 146	 }
 147	 }
...
 167	
 168	 // Process liquidation executed events
 169	 for (const event of events[5]) {
 170	 try {
 171	 const block = await provider.getBlock(event.blockNumber)
 172	 transactions.push({
 173	 id: `${event.transactionHash}-liquidate-execute`,
 174	 type: 'liquidate',
 175	 amount: formatEther(event.args.amount),
 176	 status: 'confirmed',
 177	 timestamp: block?.timestamp ? block.timestamp * 1000 : Date.now(),
 178	 hash: event.transactionHash,
 179	 description: `Position liquidated for ${formatEther(event.args.amount)} ETH`,
 180	 blockNumber: event.blockNumber
 181	 })
 182	 } catch (err) {
 183	 console.error("Error processing liquidation execute event:", err)
 184	 }
 185	 }
 186	
 187	 // Sort transactions by timestamp (newest first)
 188	 transactions.sort((a, b) => b.timestamp - a.timestamp)
 189	
 190	 console.log("Total transactions found:", transactions.length)
 191	
 192	 } catch (error) {
 193	 console.error("Error fetching transaction history:", error)
 194	 throw error
 195	 }
 196	
 197	 return transactions
 198	}
