 207	export function formatTransactionTime(timestamp: number): string {
 208	 const date = new Date(timestamp)
 209	 const now = new Date()
 210	 const diffInHours = Math.floor((now.getTime() - timestamp) / (1000 * 60 * 60))
 211	
 212	 if (diffInHours < 1) {
 213	 const diffInMinutes = Math.floor((now.getTime() - timestamp) / (1000 * 60))
 214	 return `${diffInMinutes} minutes ago`
 215	 } else if (diffInHours < 24) {
 216	 return `${diffInHours} hours ago`
 217	 } else {
 218	 const diffInDays = Math.floor(diffInHours / 24)
 219	 return `${diffInDays} days ago`
 220	 }
